	printf("          -pre:cs:mw:freqdgnss, -pre:cs:mw:freqlistdgnss, -pre:cs:mw:measdgnss, -pre:cs:mw:measlistdgnss, -pre:cs:sf:freqdgnss, -pre:cs:sf:measdgnss,\n");
