	printf("#Completion script for gLAB:\n\n");
	printf("_glab_completion()\n");
	printf("{\n");
	printf("    local cur prev cmd args\n\n");
	printf("    COMPREPLY=()\n");
	printf("    cur=${COMP_WORDS[COMP_CWORD]}\n");
